package com.squareup.wire.reflector

import assertk.assertThat
import assertk.assertions.isEqualTo
import assertk.assertions.isNull
import com.google.protobuf.DescriptorProtos.FileDescriptorProto
import com.squareup.wire.schema.Location
import com.squareup.wire.schema.Schema
import com.squareup.wire.schema.SchemaLoader
import com.squareup.wire.testing.UnwantedValueStripper
import grpc.reflection.v1alpha.ExtensionNumberResponse
import grpc.reflection.v1alpha.ExtensionRequest
import grpc.reflection.v1alpha.ServerReflectionRequest
import grpc.reflection.v1alpha.ServerReflectionResponse
import okio.ByteString.Companion.decodeBase64
import okio.FileSystem
import org.junit.Test

// Reference golang reflection tests https://github.com/juliaogris/reflect
internal class GrpcurlProto3InteropTest {
  @Test
  fun `list services`() {
    val schema = loadSchema()
    // See golden test file https://github.com/juliaogris/reflect/blob/v0.0.7/testdata/proto3-base64/TestServicesCmd.base64
    val listRespBase64 = "Cgtsb2NhbGhvc3Q6MBIPCgtsb2NhbGhvc3Q6MDoAMjoKDAoKZWNobzMuRWNobwoqCihncnBjLnJlZmxlY3Rpb24udjFhbHBoYS5TZXJ2ZXJSZWZsZWN0aW9u"
    val expectedListResponse = ServerReflectionResponse.ADAPTER.decode(listRespBase64.decodeBase64()!!)
    assertThat(
      SchemaReflector(schema, includeDependencies = false).process(
        ServerReflectionRequest(
          list_services = "",
          host = "localhost:0",
        ),
      ),
    ).isEqualTo(expectedListResponse)
  }

  val ECHO3_FILEDESCRIPTOR_RESPONSE = ""

  @Test
  fun `file_containing_symbol service`() {
    val schema = loadSchema()
    val expectedResponse = ServerReflectionResponse.ADAPTER.decode(ECHO3_FILEDESCRIPTOR_RESPONSE.decodeBase64()!!)
    val response = SchemaReflector(schema, includeDependencies = false).process(
      ServerReflectionRequest(
        file_containing_symbol = "echo3.Echo",
        host = "localhost:0",
      ),
    )
    val unwantedValueStripper = UnwantedValueStripper(clearJsonName = true)
    // TODO(juliaogirs): We should be comparing _all_ filedescriptors, not jsut the first one.
    assertThat(unwantedValueStripper.stripOptionsAndDefaults(response.fileDescriptors[0]))
      .isEqualTo(unwantedValueStripper.stripOptionsAndDefaults(expectedResponse.fileDescriptors[0]))
  }

  @Test
  fun `file_containing_symbol message`() {
    val schema = loadSchema()
    val expectedResponse = ServerReflectionResponse.ADAPTER.decode(ECHO3_FILEDESCRIPTOR_RESPONSE.decodeBase64()!!)
    val response = SchemaReflector(schema, includeDependencies = false).process(
      ServerReflectionRequest(
        file_containing_symbol = "echo3.HelloRequest",
        host = "localhost:0",
      ),
    )
    val unwantedValueStripper = UnwantedValueStripper(clearJsonName = true)
    assertThat(unwantedValueStripper.stripOptionsAndDefaults(response.fileDescriptors[0]))
      .isEqualTo(unwantedValueStripper.stripOptionsAndDefaults(expectedResponse.fileDescriptors[0]))
  }

  @Test
  fun `file_containing_symbol rpc`() {
    val schema = loadSchema()
    val expectedResponse = ServerReflectionResponse.ADAPTER.decode(ECHO3_FILEDESCRIPTOR_RESPONSE.decodeBase64()!!)
    val response = SchemaReflector(schema, includeDependencies = false).process(
      ServerReflectionRequest(
        file_containing_symbol = "echo3.Echo.HelloStream",
        host = "localhost:0",
      ),
    )
    val unwantedValueStripper = UnwantedValueStripper(clearJsonName = true)
    assertThat(unwantedValueStripper.stripOptionsAndDefaults(response.fileDescriptors[0]))
      .isEqualTo(unwantedValueStripper.stripOptionsAndDefaults(expectedResponse.fileDescriptors[0]))
  }

  val GOOGLE_API_RPC_RESPONSE = ""

  @Test
  fun `file_containing_symbol rpc option`() {
    val schema = loadSchema()
    val expectedResponse = ServerReflectionResponse.ADAPTER.decode(GOOGLE_API_RPC_RESPONSE.decodeBase64()!!)
    val response = SchemaReflector(schema, includeDependencies = false).process(
      ServerReflectionRequest(
        file_containing_symbol = "google.api.http",
        host = "localhost:0",
      ),
    )
    val unwantedValueStripper = UnwantedValueStripper(clearJsonName = true)
    assertThat(unwantedValueStripper.stripOptionsAndDefaults(response.fileDescriptors[0]))
      .isEqualTo(unwantedValueStripper.stripOptionsAndDefaults(expectedResponse.fileDescriptors[0]))
  }

  @Test
  fun `file_by_filename`() {
    val schema = loadSchema()
    val expectedResponse = ServerReflectionResponse.ADAPTER.decode(ECHO3_FILEDESCRIPTOR_RESPONSE.decodeBase64()!!)
    val response = SchemaReflector(schema, includeDependencies = false).process(
      ServerReflectionRequest(
        file_by_filename = "echo3/echo3.proto",
        host = "localhost:0",
      ),
    )
    val unwantedValueStripper = UnwantedValueStripper(clearJsonName = true)
    assertThat(unwantedValueStripper.stripOptionsAndDefaults(response.fileDescriptors[0]))
      .isEqualTo(unwantedValueStripper.stripOptionsAndDefaults(expectedResponse.fileDescriptors[0]))
  }

  @Test
  fun `all_extension_numbers_of_type`() {
    val schema = loadSchema()
    val response = SchemaReflector(schema, includeDependencies = false).process(
      ServerReflectionRequest(
        all_extension_numbers_of_type = "google.protobuf.MethodOptions",
        host = "localhost:0",
      ),
    )

    assertThat(response.error_response).isNull()
    assertThat(response.all_extension_numbers_response).isEqualTo(
      ExtensionNumberResponse(
        base_type_name = "google.protobuf.MethodOptions",
        extension_number = listOf(72295728),
      ),
    )
  }

  @Test
  fun `file_containing_extension`() {
    val schema = loadSchema()
    val extensionsFileDescriptorResponse = ""
    val expectedResponse = ServerReflectionResponse.ADAPTER.decode(extensionsFileDescriptorResponse.decodeBase64()!!)
    val response = SchemaReflector(schema, includeDependencies = false).process(
      ServerReflectionRequest(
        file_containing_extension = ExtensionRequest(
          containing_type = "google.protobuf.MethodOptions",
          extension_number = 72295728,
        ),
        host = "localhost:0",
      ),
    )
    val unwantedValueStripper = UnwantedValueStripper(clearJsonName = true)
    assertThat(unwantedValueStripper.stripOptionsAndDefaults(response.fileDescriptors[0]))
      .isEqualTo(unwantedValueStripper.stripOptionsAndDefaults(expectedResponse.fileDescriptors[0]))
  }

  private val ServerReflectionResponse.fileDescriptors
    get() = file_descriptor_response!!.file_descriptor_proto.map {
      FileDescriptorProto.parseFrom(
        it.toByteArray(),
      )
    }
}

private fun loadSchema(): Schema {
  return SchemaLoader(FileSystem.SYSTEM)
    .apply {
      initRoots(
        sourcePath = listOf(
          Location.get("src/main/resources", "grpc/reflection/v1alpha/reflection.proto"),
          Location.get("src/test/proto", "echo3/echo3.proto"),
          Location.get("src/test/proto", "google/api/annotations.proto"),
          Location.get("src/test/proto", "google/api/http.proto"),
          Location.get("src/test/proto", "google/protobuf/any.proto"),
        ),
        protoPath = listOf(),
      )
    }
    .loadSchema()
}
